record.field:
        

// config
let config = this.get_config();
// get the datasource
let data_source = this.get_data_source();
// get the catorgories
let categories = data_source.get_categories();
if (categories.length == 0) {
  return;
}

// get the measures
let measures = data_source.get_measures()

let series = [];
for (let i = 0; i < measures.length; i++) {
  let measure = measures[i];
  let raw_field = data_source.get_raw_field(measure);
  series.push({
    type: raw_field && raw_field.series_type || 'bar',
    data: data_source.get_col_values(measure),
    coordinateSystem: 'polar',
    label: {
      show: true,
      position: 'middle',
      formatter: '{b}: {c}'
    }
  })
}

let option = {
  title: [
    {
      text: config.get_title(),
    }
  ],
  polar: {
    radius: [30, '80%']
  },
  radiusAxis: {
    max: 4
  },
  angleAxis: {
    type: 'category',
    data: data_source.get_col_values(categories[0]),
    startAngle: 75
  },
  tooltip: {},
  series: series,
  animation: false
};

this.setOption(option)

        
