// get the datasource
let data_source = this.get_data_source()
if (!data_source) {
  return;
}

// get the catorgories
let categories = data_source.get_categories()
if (categories.length == 0) {
  return;
}

// get the measures
let measures = data_source.get_measures()
if (data_source.fields_as_category()) {
  let series = []
  let records = data_source.get_records()
  for (var i=0; i < records.length; i++) {
    let record = records[i]
    let series_data = []
    for (let j = 0; j < measures.length; j++) {
      series_data.push(record.get_value(measures[j]))
    }
    series.push({
      data: series_data,
      type: 'bar',
      showBackground: true,
      backgroundStyle: {
        color: 'rgba(180, 180, 180, 0.2)'
      }
    })
  }
  let option = {
    xAxis: {
      type: 'value',
      data: data_source.convert_to_alias(categories)
    },
    yAxis: {
      type: 'category',
    },
    series: series
  };

  this.setOption(option)
} else {
  let series = []
  for (let i = 0; i < measures.length; i++) {
    let measure = measures[i]
    let raw_field = data_source.get_raw_field(measures[i])
    series.push({
        data: data_source.get_col_values(measure),
        type: raw_field && raw_field.series_type ? raw_field.series_type : 'bar',
        showBackground: true,
        name: measure,
        backgroundStyle: {
          color: 'rgba(180, 180, 180, 0.2)'
        }
    })
  }
  
  // generate the option, you can change the title, legend, tooltip, etc.
  let option = {
    xAxis: {
      type: 'value',
      data: data_source.get_col_values(categories[0])
    },
    yAxis: {
      type: 'category',
      data: data_source.get_col_values(categories[0]),
    },
    series: series
  };

  this.setOption(option)
}

        
let option = {
  title: {
    text: 'World Population'
  },
  tooltip: {
    trigger: 'axis',
    axisPointer: {
      type: 'shadow'
    }
  },
  legend: {},
  grid: {
    left: '3%',
    right: '4%',
    bottom: '3%',
    containLabel: true
  },
  xAxis: {
    type: 'value',
    boundaryGap: [0, 0.01]
  },
  yAxis: {
    type: 'category',
    data: ['Brazil', 'Indonesia', 'USA', 'India', 'China', 'World']
  },
  series: [
    {
      name: '2011',
      type: 'bar',
      data: [18203, 23489, 29034, 104970, 131744, 630230]
    },
    {
      name: '2012',
      type: 'bar',
      data: [19325, 23438, 31000, 121594, 134141, 681807]
    }
  ]
};
